import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import classnames from 'classnames';
import { ArrowDownIcon } from '@heroicons/react/solid';

const PLAYERS = [
  { first_name: 'John', last_name: 'Hicks', number: 22, graduation_year: 2024, }
]

export default function Players() {
    let params = useParams();
    console.log(params, 'param')
    return (
        <main style={{ padding: "1rem 0" }}>
            <h2>Players: {params.players}</h2>


<Table />
            {/* <div className="flex">
                {PLAYERS.map(item => {
                    return (
                        <div className="p-2 bg-white mx-2">
                            <h3 className="text-center">
                                {item.first_name} {item.last_name}
                            </h3>

                        </div>
                    )
                })}
            </div> */}
        </main>
    );
}

const Table = () => {

    const headers = [
        { label: 'Order #', sort: 'id', className: 'whitespace-nowrap' },
        { label: 'Date', sort: 'date', default: true },
        { label: 'Name', sort: 'name' },
        { label: 'Email', sort: 'email' },
        { label: 'Amount', sort: 'amount' },
        { label: 'Subscription', sort: 'subscription', className: 'justify-center flex' },
        { label: '', sort: 'null', className: 'w-0' },
    ];

    const [sortBy, setSortBy] = useState(headers.find(h => h.default)?.sort || null);
    const [sortDir, setSortDir] = useState('asc');

    // useEffect(() => {
    //     // set ordering if query params exist
    //     const queryParams = {...router.query};
    //     // delete queryParams.tab

    //     if (Object.keys(queryParams).length !== 0) {
    //         setSortBy(queryParams.orderby);
    //         setSortDir(queryParams.dir);
    //     }
    // }, [])

    const applySort = (sortField) => {
        let direction = sortDir === 'asc' ? 'desc' : 'asc'
        if (sortField !== sortBy) {
            direction = 'asc'
        }
        console.log(sortField, 'sortField')
        setSortBy(sortField);
        setSortDir(direction);

        // router.push({ query: { ...router.query, orderby: sortField, dir: direction }});
    }


    const TableSort = ({ label, sort, className }) => {
        const isSortable = sort !== undefined;
        const active = sort === sortBy;
        return (
            <th className={`p-2 ${className}`}>
                <div
                    onClick={() => !!sort && applySort(sort)}
                    className={classnames(
                        `font-semibold text-left relative`,
                        {
                            'hover:underline cursor-pointer': !!sort,
                        }
                    )} 
                >
                    {label}

                    <ArrowDownIcon className={classnames(
                        `inline h-3 mx-1 absolute top-px text-indigo-500`,
                        {
                            'invisible': !isSortable || !active,
                            'rotate-180': sortDir === 'desc',
                        }
                        )} 
                    />
                </div> 
            </th>
        )
    }

    return (
        <div className="col-span-full xl:col-span-6 bg-white shadow-lg rounded-sm border border-gray-200 mb-8">
            {/* {title && ( */}
                <div className="px-5 py-4 border-b border-gray-200 bg-emerald-200">
                    <h2 className="font-semibold text-gray-800">Players - (custom table)</h2>
                </div>
            {/* )} */}
        {/* <h2>Sortable Table component</h2> */}

            <div className="p-3">
                <div className="overflow-x-auto">
                    <table className="table-auto w-full">
                        <thead className="text-xs uppercase text-gray-400 bg-gray-50 rounded-sm">
                            <tr>
                                {headers.map(item => {
                                    return (
                                        <TableSort label={item.label} sort={item.sort} className={item.className} key={item.label} />
                                    )
                                })}
                            </tr>
                        </thead>

                        <tbody className="text-sm font-medium divide-y divide-gray-100">

                            {/* {body.map((item, ind) => {
                                return (
                                    <tr key={uniqueKey ? item[uniqueKey] :item.id}>
                                        {Object.entries(columns).map(([colKey, colValue]) => {
                                            // console.log(colKey, colValue, 'COL')
                                            let formattedRow;
                                            if (typeof colValue === 'object') {
                                                formattedRow = switchType(colValue.type, colValue, item[colKey], item, ind)
                                                return (
                                                    <td className={`p-2 ${colValue.className}`} key={colKey}>{formattedRow}</td>
                                                )
                                            } 

                                            formattedRow = switchType(colValue, item[colKey], null, null, ind);
                                            // console.log(stuff, 'STUFF!!!')
