    penalties_in_minutes: 0,
}

const initialStateTypes = {
    goals: 'Goals',
    assists: 'Assists',
    sog: 'Shots on Goal',
    ground_balls: 'Ground Balls',
    interceptions: 'Interceptions',
    takeaways: 'Takeaways',
    unforced_errors: 'Unforced Errors',
    penalties: 'Penalties',
    penalties_in_minutes: 'Penalties In Minutes',
}

const PLAYERS = [
    { id: 1, first_name: 'John', last_name: 'Hicks', number: 22, graduation_year: 2024 },
    { id: 2, first_name: 'Aiden', last_name: 'Carlock', number: 24, graduation_year: 2024 },
    { id: 3, first_name: 'Jack', last_name: 'Cendoya', number: 55, graduation_year: 2022 },
