// import toRegexRange from 'to-regex-range';
// import { getDatabase, ref, set } from "firebase/database";
import { useDispatch, useSelector } from 'react-redux'
// import { customAlphabet } from 'nanoid'
import { useForm } from "../../hooks";
// import { request } from '../../request';
import { addPlayerGameStats } from '../../redux/slices/gamesSlice';
import { Select } from '../';
import { useState } from 'react';

// const validFromYear = new Date().getFullYear() - 3;
// const validToYear = validFromYear + 6;
// const validYearsRegex = toRegexRange(validFromYear, validToYear, { capture: true });

const initialState = {
    goals: 0,
    assists: 0,
    sog: 0,
    ground_balls: 0,
    interceptions: 0,
    takeaways: 0,
    unforced_errors: 0,
    penalties: 0,
    penalties_in_minutes: 0,
}

const initialStateTypes = {
    goals: 'Goals',
    assists: 'Assists',
    sog: 'Shots on Goal',
    ground_balls: 'Ground Balls',
    interceptions: 'Interceptions',
    takeaways: 'Takeaways',
    unforced_errors: 'Unforced Errors',
    penalties: 'Penalties',
    penalties_in_minutes: 'Penalties In Minutes',
}

// const PLAYERS = [
//     { id: 1, first_name: 'John', last_name: 'Hicks', number: 22, graduation_year: 2024 },
//     { id: 2, first_name: 'Aiden', last_name: 'Carlock', number: 24, graduation_year: 2024 },
//     { id: 3, first_name: 'Jack', last_name: 'Cendoya', number: 55, graduation_year: 2022 },
//     { id: 43, first_name: 'Scott', last_name: 'Common', number: 23, graduation_year: 2022 },
//     { id: 53, first_name: 'Oscar', last_name: 'Jackson', number: 53, graduation_year: 2022 },
//     { id: 35, first_name: 'Adam', last_name: 'Wold', number: 51, graduation_year: 2022 },
//     { id: 335, first_name: 'Kris', last_name: 'Johnson', number: 19, graduation_year: 2022 },
//     { id: 223, first_name: 'Larkin', last_name: 'Gray', number: 5, graduation_year: 2022 },
//     { id: 13, first_name: 'Martin', last_name: 'Santana', number: 2, graduation_year: 2022 },
// ]

// const playersAlreadyPlaying = [
//     { id: 1, first_name: 'John', last_name: 'Hicks', number: 22, graduation_year: 2024, goals: 3, assists: 1, points: 4 },
//     { id: 2, first_name: 'Aiden', last_name: 'Carlock', number: 24, graduation_year: 2024, goals: 3, assists: 2, points: 5 },
//     { id: 3, first_name: 'Jack', last_name: 'Cendoya', number: 55, graduation_year: 2022, goals: 1, assists: 0, points: 1 },
// ];


// const playerFilter = playersAlreadyPlaying.map(item => {
//     return item.id
// })





const AddPlayerGameStatsModal = ({ closeModal, playersAlreadyPlaying2, gameId: game_id, seasonId: season_id }) => {
    // const { fields, handleChange, errors, validate } = useForm(initialState);
    // const { last_name, first_name, number, ...rest } = player;
    const [selectedValue, setSelectedValue] = useState(null);
    const [showDropdownError, setShowDropdownError] = useState(false);
    const { fields, handleChange, errors, validate } = useForm({ ...initialState });
    const dispatch = useDispatch();

    const { players } = useSelector(state => state.players);

    console.log(players, 'playerssssss')
    console.log(playersAlreadyPlaying2, 'playersAlreadyPlaying2playersAlreadyPlaying2');



    const handleSubmit = (e) => {
        e.preventDefault();
        // const isValidated = validate(validations)
        // console.log(isValidated, 'isValidated')
        // console.log(fields, 'fields!!!!!')
        // console.log(errors, 'errors!!!!!')

        // console.log({...player, ...fields, game_id, season_id},' fields')
        // if (!isValidated) return;

        if (!selectedValue) {
            setShowDropdownError(true);
        }

        // console.log('SUBMIT FUNCTION HERE!!!')
        const playerStats = { ...selectedValue.value, ...fields, game_id, season_id };
        // console.log(playerStats, 'playerStatsplayerStats')
        // await dispatch and show loader until success
        dispatch(addPlayerGameStats(playerStats))
        closeModal();
    }

    const renderInputs = () => {
        return Object.keys(initialState).map(item => {
            return (
                <div className="mb-2 items-center w-full" key={item}>
                    <label htmlFor={item} className="text-sm text-gray-800 w-5/12 inline-block">{initialStateTypes[item]}</label>
                    <input value={fields[item]} min={0} onChange={handleChange} type="number" name={item} id={item} className="mt-1 w-7/12 rounded form-input border border-gray-300 px-3 py-1 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400" />
                </div>
            )
        })
    }

    const playerFilter2 = playersAlreadyPlaying2.map(item => {
        return item.player_id
    })

    // console.log(playerFilter2, 'playerFilter2')

    const filtered2 = players.filter(item => {
        return !playerFilter2.includes(item.player_id)
    })

    // console.log(filtered2, 'THE SECOND LOL')

    // const filtered = PLAYERS.filter(item => {
    //     return !playerFilter.includes(item.id)
    // })

    const optionsExample = filtered2.map(item => {
        return { value: { player_id: item.player_id, player_number: item.player_number }, label: `${item.first_name} ${item.last_name} #${item.player_number}`}
    })

    return (
        <div className="bg-red-10">
            <div className="px-4 pt-4 pb-3 border-b border-gray-300">
                <h3 className="font-bold">Add Player Stats</h3>
            </div>
            <form onSubmit={handleSubmit}>
                <div className="p-4">
                    {/* <div className="mb-2">
                        <label className="text-sm text-gray-800">Season</label>
                        <Select options={optionsExample} onChange={e => setSelectedSeason(e)} value={selectedSeason} />
                    </div> */}

                    <Select
                        options={optionsExample}
                        onChange={option => {
                            setSelectedValue(option)
                            setShowDropdownError(false);
                        }}
                        value={selectedValue}
                    />
                    <p className={`pt-0.5 mb-3 text-xs text-mpred transition duration-300 ${showDropdownError ? 'text-opacity-100' : 'text-opacity-0'}`}>Player required<span className="after:content-['.'] invisible"></span></p>


                    {renderInputs()}

                </div>

                <div className="px-4 pb-4 pt-3 border-t border-gray-300 flex justify-end">
                    <button
                        type="button"
                        onClick={closeModal}
                        className="transition duration-300 text-mpred py-1 px-3 mr-5 hover:text-mpblue hover:bg-transparent"
                    >
                        Cancel
                    </button>
                    <button
                        // onClick={closeModal}
                        type="submit"
                        className="transition duration-300 border border-mpblue text-white py-1 px-3 bg-mpblue hover:text-mpblue hover:bg-transparent"
                    >
                        Add
                    </button>
                </div>
            </form>
        </div>
    )
}

export default AddPlayerGameStatsModal;
