                        <input value={fields.last_name} onChange={handleChange} type="text" name="last_name" id="last_name" className="mt-1 rounded form-input border border-gray-300 px-3 py-1 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400" />
