// import { useState, useEffect } from "react";
import { useState } from "react";
import { useParams } from "react-router-dom";
// import classnames from 'classnames';
import { Table , Select, Title, Modal } from "../components";

const PLAYERS = [
  { id: 1, first_name: 'John', last_name: 'Hicks', number: 22, graduation_year: 2024, games_played: 4, goals: 19, goals_per_game: 4.75, assists: 4, assists_per_game: 1, points: 23, points_per_game: 5.8 },
  { id: 2, first_name: 'Aiden', last_name: 'Carlock', number: 24, graduation_year: 2024, games_played: 6, goals: 9, goals_per_game: 1.5, assists: 1, assists_per_game: 0.1, points: 10, points_per_game: 1.6 },
  { id: 3, first_name: 'Jack', last_name: 'Cendoya', number: 55, graduation_year: 2022, games_played: 6, goals: 1, goals_per_game: 0.1, assists: 0, assists_per_game: 0, points: 1, points_per_game: 0.1 },
]

export default function Players() {
    const [selectedSeason, setSelectedSeason] = useState({ value: 525, label: '2022 - [current]' });
    const [showCreatePlayerModal, setShowCreatePlayerModal] = useState(false);
    const params = useParams();
    console.log(params, 'param')

    const headers = [
        { label: '#', sort: 'number', className: 'whitespace-nowrap', default: true },
        { label: 'Player', sort: 'last_name', className: 'whitespace-nowrap' },
        { label: 'GP', sort: 'games_played', alt: 'Games Played' },
        { label: 'G', sort: 'goals' },
        { label: 'G/G', sort: 'goals_per_game' },
        { label: 'A', sort: 'assists' },
        { label: 'A/G', sort: 'assists_per_game' },
        { label: 'PTS', sort: 'points' },
        { label: 'P/G', sort: 'points_per_game' },
    ];

    const columns = {
        number: 'number',
        name: {
            type: 'link',
            format: '/players/$id',
            as: '$first_name $last_name',
            // as: 'View Details',
            // className: 'w-0 whitespace-nowrap'
        },
        games_played: 'number',
        goals: 'number',
        goals_per_game: 'number',
        assists: 'number',
        assists_per_game: 'number',
        points: 'number',
        points_per_game: 'number',

        // email: 'string',
        // amount: 'currency',
        // subscription: {
        //     type: 'boolean',
        //     format: <svg className="transition duration-200 opacity-1 w-4 h-4 text-purple pointer-events-none" viewBox="0 0 172 172"><g fill="none" strokeWidth="none" strokeMiterlimit="10" fontFamily="none" fontWeight="none" fontSize="none" textAnchor="none" style={{mixBlendMode:'normal'}}><path d="M0 172V0h172v172z"/><path d="M145.433 37.933L64.5 118.8658 33.7337 88.0996l-10.134 10.1341L64.5 139.1341l91.067-91.067z" fill="currentColor" strokeWidth="1"/></g></svg>,
        //     className: 'justify-center flex',
        // },
    }

    const optionsExample = [
        { value: 525, label: '2022 - [current]' },
        { value: 5253, label: '2021' },
        { value: 52534, label: '2020' },
        { value: 2352, label: '2019' },
        { value: 3423, label: '2018' },
    ]



    return (
        // <main style={{ padding: "1rem 0" }}>
        <main className="py-6">

            <Modal isOpen={showCreatePlayerModal} onClose={setShowCreatePlayerModal}>

                <div className="bg-red-100">
                    <div className="px-4 pt-4 pb-2 border-b border-mpblue">
                        <h3>Create Player</h3>
                    </div>
                    <div className="p-4">
                        <div className="mb-2">
                            <label htmlFor="first_name" className="text-sm text-gray-500">First Name</label>
                            <input type="text" name="first_name" id="first_name" className="mt-2 rounded form-input border w-full px-3 py-2 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400" />
                            <p className={`pt-0.5 text-xs text-red-500 transition duration-300 text-opacity-100`}>rrors.name<span className="after:content-['.'] invisible"></span></p>
                            {/* <p className={`pt-0.5 text-xs text-red-500 transition duration-300 ${!!errors.name ? 'text-opacity-100' : 'text-opacity-0'}`}>{errors.name}<span className="after:content-['.'] invisible"></span></p> */}
                        </div>

                        <div className="mb-2">
                            <label htmlFor="last_name" className="text-sm text-gray-500">Last Name</label>
                            <input type="text" name="last_name" id="last_name" className="mt-2 rounded form-input border w-full px-3 py-2 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400" />
                            {/* <p className={`pt-0.5 text-xs text-red-500 transition duration-300 text-opacity-100`}>rrors.name<span className="after:content-['.'] invisible"></span></p> */}
                            {/* <p className={`pt-0.5 text-xs text-red-500 transition duration-300 ${!!errors.name ? 'text-opacity-100' : 'text-opacity-0'}`}>{errors.name}<span className="after:content-['.'] invisible"></span></p> */}
                        </div>

                        {/* <label htmlFor="last_name" className="text-sm text-gray-500">Name</label>
                        <input type="text" name="last_name" id="last_name" className="mb-5 mt-2 rounded form-input border w-full px-3 py-2 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400" /> */}
                    </div>
                    <div className="px-4 pb-4 pt-2 border-t border-mpblue flex justify-end">
                        <button
                            onClick={() => setShowCreatePlayerModal(true)}
                            className="transition duration-300 text-mpred py-1 px-3 hover:text-mpblue hover:bg-transparent"
                        >
                            Cancel
                        </button>
                        <button
                            onClick={() => setShowCreatePlayerModal(false)}
                            className="transition duration-300 border border-mpblue text-white py-1 px-3 bg-mpblue hover:text-mpblue hover:bg-transparent"
                        >
                            Create Player
                        </button>
                    </div>


                </div>

            </Modal>

            <div className="flex justify-end">
                <button
                    onClick={() => setShowCreatePlayerModal(true)}
                    className="transition duration-300 border border-mpblue text-mpblue py-1 px-3 mb-4  hover:text-white hover:bg-mpblue"
                >
                    Create Player
                </button>
            </div>

            <div className="bg-white p-3 mb-6 sm:p-6">
                <div className="justify-between mb-3 sm:flex">
                    <Title>Players</Title>

                    <div className="flex items-center">
                        <p className="text-sm">Season: &nbsp;</p>
                        <div className="w-full mb-6 sm:mb-0 sm:w-48">
                            <Select options={optionsExample} onChange={e => setSelectedSeason(e)} value={selectedSeason} />
                        </div>
                    </div>

                </div>

                <Table
                    headers={headers}
                    columns={columns}
                    body={PLAYERS}
                    // title="Players"
                />
            </div>
        </main>
    );
}
