import { useState, useEffect } from "react";
import { Table, Title, Modal } from "../components";
import { EditPlayerStatsModal, AddPlayerGameStatsModal } from "../components/modals";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { format, parseISO } from 'date-fns' 
import { getGameById } from '../redux/slices/gamesSlices';

import classNames from "classnames";

const GAME = {
    opponent: 'Alpharetta HS',
    opponent_id: 2432,
    location: '1234 10th st, Alpharetta',
    date: 'February 18th, 2022',
    has_been_played: false,
    scores: {
        us: [2, 4, 3, 7, 16],
        opponent: [2, 2, 5, 2, 11],
    },
    team_stats: {
        goals: { us: 16, opponent: 11 },
        ground_balls: { us: 10, opponent: 16 },
        shots: { us: 24, opponent: 22 },
        faceoffs_won: { us: 20, opponent: 11 },
        penalties_in_minutes: { us: 2.5, opponent: 5 }
    },
    player_stats: [
        { id: 1, first_name: 'John', last_name: 'Hicks', number: 22, graduation_year: 2024, goals: 3, assists: 1, points: 4 },
        { id: 2, first_name: 'Aiden', last_name: 'Carlock', number: 24, graduation_year: 2024, goals: 3, assists: 2, points: 5 },
        { id: 3, first_name: 'Jack', last_name: 'Cendoya', number: 55, graduation_year: 2022, goals: 1, assists: 0, points: 1 },
    ],
    player_stats2: {
        245335: { first_name: 'John', last_name: 'Hicks', number: 22, graduation_year: 2024, goals: 3, assists: 1, points: 4 },
        259924: { first_name: 'Aiden', last_name: 'Carlock', number: 24, graduation_year: 2024, goals: 3, assists: 2, points: 5 },
        385300: { first_name: 'Jack', last_name: 'Cendoya', number: 55, graduation_year: 2022, goals: 1, assists: 0, points: 1 },
    },
    goalie_stats: [
        { id: 12, first_name: 'Matthew', last_name: 'Davidson', number: 5, graduation_year: 2022, minutes: 48, shots_on_goal: 22, saves: 11, save_percentage: 0.5, goals_against: 11, goals_against_percentage: 0.5 },
        // { id: 24, first_name: 'Aiden', last_name: 'Carlock', number: 24, graduation_year: 2024, goals: 3, assists: 2, points: 5 },
    ]
}



// save percentage calc = saves / shots on goal
const Game = () => {
    const [isEditing, setIsEditing] = useState(false);
    const [showEditPlayerStatsModal, setShowEditPlayerStatsModal] = useState(false);
    const [showAddPlayerStatsModal, setShowAddPlayerStatsModal] = useState(false);
    const [selectedPlayer, setSelectedPlayer] = useState(null);
    const [checkedPlayersInGame, setCheckedPlayersInGame] = useState([]);
    const dispatch = useDispatch();
    const params = useParams();

    // const { game } = useSelector(state => state.games);

    const { game, players } = useSelector(state => ({ ...state.games,  ...state.players }));

    console.log(game, ' SINGGLE GAMEE =====')

    useEffect(() => {
        dispatch(getGameById(params.game_id))
    }, [dispatch, params.game_id])

    const { scores, team_stats, goalie_stats } = GAME;

    const { has_been_played, opponent, location, start_date, player_stats } = game;


    const usScores = Object.keys(game).filter(ele => ele.startsWith('us_') && ele.includes('_scores_') && !ele.includes('_scores_overtime') );
    const opponentScores = Object.keys(game).filter(ele => ele.startsWith('opponent_') && ele.includes('_scores_') && !ele.includes('_scores_overtime') );

    const playerHeaders = [
        { label: '#', sort: 'number', className: 'whitespace-nowrap w-0', default: true },
        { label: 'Player', sort: 'last_name', className: 'whitespace-nowrap' },
        { label: 'G', sort: 'goals' },
        { label: 'A', sort: 'assists' },
        { label: 'PTS', sort: 'points' },
    ];

    const playerColumns = {
        number: 'number',
        name: {
            type: 'link',
            format: '/players/$id',
            as: '$first_name $last_name',
            className: 'whitespace-nowrap'
        },
        goals: 'number',
        assists: 'number',
        // points: 'number',



        // might want to change this and have it look through ad add a "points" key to the game object
        // currenty if sorting by points, will error out
        points: {
            type: 'math',
            format: `$goals + $assists`
        },
    }

    if (isEditing) {
        playerHeaders.push({ label: '' })
        playerColumns.edit = {
            type: 'button',
            func: (e) => {
                setShowEditPlayerStatsModal(true)
                setSelectedPlayer(e)
                // console.log(e,'hellow')
            },
            // component: (
            //     <button onClick={(e) => console.log(e, 'thisss')} className="text-mpred">hello world</button>
            // ),
            as: 'Edit',
            className: 'w-0 whitespace-nowrap'
        }
    }


    const goalieHeaders = [
        { label: '#', className: 'whitespace-nowrap w-0', default: true },
        { label: 'Player', className: 'whitespace-nowrap' },
        { label: 'SOG' },
        { label: 'SV' },
        { label: 'SV%' },
        { label: 'GA' },
        { label: 'GA%' },
        { label: 'Min' },
    ];


    const goalieColumns = {
        number: 'number',
        name: {
            type: 'link',
            format: '/players/$id',
            as: '$first_name $last_name',
            className: 'whitespace-nowrap'
        },
        shots_on_goal: 'number',
        saves: 'number',
        save_percentage: 'number',
        goals_against: 'number',
        goals_against_percentage: 'number',
        minutes: 'number',
    }

    const handleCheckboxChange = playerId => {
        let newArray = [...checkedPlayersInGame, playerId];
        if (checkedPlayersInGame.includes(playerId)) {
          newArray = newArray.filter(day => day !== playerId);
        } 
        setCheckedPlayersInGame(newArray)
    };

    return (
        <main className="py-6">

            <div className="flex justify-end">
                <button
                    onClick={() => setIsEditing(!isEditing)}
                    // className="transition duration-300 border border-mpblue text-mpblue py-1 px-3 mb-4  hover:text-white hover:bg-mpblue"
                    className={classNames("transition duration-300 border border-mpblue text-mpblue py-1 px-3 mb-4  hover:text-white hover:bg-mpblue", {
                        "invisible" : isEditing
                    })}
                >
                    Edit Game
                </button>
            </div>

            {isEditing && !game.has_been_played && (
                <>
                    <div className="bg-white p-3 sm:p-6 mb-3 sm:mb-6 shadow-sm">

                        <p>show list of players</p>

                        {players.map(item => {
                            return (
                                <div className="my-2 flex">
                                    {/* <input checked={fields.is_home} onChange={handleChange} type="checkbox" maxLength={4} name="is_home" id="is_home" className="mt-1 rounded form-input border border-gray-300 px-3 py-1 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400" /> */}
                                    <input type="checkbox" id={item.player_id} onChange={() => handleCheckboxChange(item.player_id)} className="mt-1 cursor-pointer rounded form-input border border-gray-300 px-3 py-1 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400" />
                                    <label htmlFor={item.player_id} className="pl-3 text-sm text-gray-800 cursor-pointer">{item.first_name} {item.last_name}</label>
                                </div>
                            )
                        })}
                    </div>

                    <div className="flex justify-end">

                        <button
                            type="button"
                            onClick={() => setIsEditing(!isEditing)}
                            className="transition duration-300 text-mpred py-1 px-3 mr-5 hover:text-mpblue hover:bg-transparent"
                        >
                            Cancel
                        </button>
                        <button
                            onClick={() => setIsEditing(!isEditing)}
                            className="transition duration-300 border border-mpblue text-white py-1 px-3 text-mpblue hover:bg-mpblue hover:text-white"
                            // className="transition duration-300 border border-mpblue text-mpblue py-1 px-3 mb-4  hover:text-white hover:bg-mpblue"
                            // className="transition duration-300 text-mpred py-1 px-3 mr-5 hover:text-mpblue hover:bg-transparent"
                        >
                            Edit Game
                        </button>
                    </div>
                </>
            )}

            {isEditing && game.has_been_played && (
                <>
                    <Modal isOpen={showEditPlayerStatsModal} onClose={setShowEditPlayerStatsModal}>
                        {(closeModal) => <EditPlayerStatsModal closeModal={closeModal} player={selectedPlayer} gameId={game.game_id} seasonId={game.season_id} />}
                    </Modal>

                    <Modal isOpen={showAddPlayerStatsModal} onClose={setShowAddPlayerStatsModal}>
                        {(closeModal) => <AddPlayerGameStatsModal closeModal={closeModal} playersAlreadyPlaying2={player_stats} gameId={game.game_id} seasonId={game.season_id} />}
                    </Modal>

                    <div className="bg-white p-3 sm:p-6 mb-3 sm:mb-6 shadow-sm">
                        <div className="sm:flex sm:gap-5">
                            <div className="mb-2 w-full">
                                <label htmlFor="opponent" className="text-sm text-gray-800">Opponent</label>
                                <input value={opponent} type="text" name="opponent" id="opponent" className="mt-2 rounded form-input border border-gray-300 w-full px-3 py-1 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400" />
                                <p className={`pt-0.5 text-xs text-mpred transition duration-300 text-opacity-100`}>exmple of an error<span className="after:content-['.'] invisible"></span></p>
                                {/* <p className={`pt-0.5 text-xs text-mpred transition duration-300 ${!!errors.name ? 'text-opacity-100' : 'text-opacity-0'}`}>{errors.name}<span className="after:content-['.'] invisible"></span></p> */}
                            </div>

                            <div className="mb-2 w-full">
                                <label htmlFor="location" className="text-sm text-gray-800">Location</label>
                                <input value={location} type="text" name="location" id="location" className="mt-2 rounded form-input border border-gray-300 w-full px-3 py-1 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400" />
                            </div>
                        </div>
                    </div>

                    <div className="bg-white p-3 sm:p-6 mb-3 sm:mb-6 shadow-sm">
                        {/* <Title>Player Stats - Edit</Title> */}

                        {/* <div className="bg-white p-3 sm:p-6 mb-6 shadow-sm overflow-scroll"> */}
                            <Table
                                headers={playerHeaders}
                                columns={playerColumns}
                                body={player_stats || []}
                                title="Player Stats - Edit"
                                disableSort
                            />
                        {/* </div> */}
                        <div className="flex items-center mt-6 mb-4 ">
                            <button
                                onClick={() => setShowAddPlayerStatsModal(!showAddPlayerStatsModal)}
                                className="transition duration-300 border-mpblue text-mpblue py-1 px-3 hover:text-white hover:bg-mpblue disabled:bg-mpblue disabled:text-white disabled:opacity-30"
                                disabled={players?.length < game.player_stats?.length}
                            >
                                + Add Player
                            </button>

                            {players.length < game.player_stats?.length && (
                                <p className="ml-4">All current players are playing in this game</p>
                            )}
                        </div>

                    </div>


                    <div className="bg-white p-3 sm:p-6 mb-3 sm:mb-6 shadow-sm">
                        <Title>Goalie Stats - Edit</Title>
                        <div className="flex">
                            <button
                                onClick={() => setIsEditing(!isEditing)}
                                className="transition duration-300  border-mpblue text-mpblue py-1 px-3 mb-4  hover:text-white hover:bg-mpblue"
                            >
                                + Add Goalie
                            </button>
                        </div>

                    </div>



                    <div className="flex justify-end">
                        {/* <button
                            onClick={() => setIsEditing(!isEditing)}
                            className="transition duration-300 border border-mpblue text-mpblue py-1 px-3 mb-4  hover:text-white hover:bg-mpblue"
                        >
                            Edit Game
                        </button> */}

                        <button
                            type="button"
                            onClick={() => setIsEditing(!isEditing)}
                            className="transition duration-300 text-mpred py-1 px-3 mr-5 hover:text-mpblue hover:bg-transparent"
                        >
                            Cancel
                        </button>
                    </div>
                </>

            )}

            {!isEditing && (
                <>
                    <div className="bg-white p-3 sm:p-6 mb-3 sm:mb-6 sm:flex flex-wrap items-center justify-between shadow-sm">

                        <div className="pb-6 md:pb-0 text-center">
                            <h3 className="text-3xl">{opponent}</h3>
                            <h4 className="text-sm">{location}</h4>
                            {/* <h4 className="text-sm">{start_date}</h4> */}
                            <h4 className="text-sm">{start_date && format(parseISO(start_date), "PPPP @ pp")}</h4>


                        </div>

                        {has_been_played && (
                            <div className="overflow-scroll">
                                <table className="m-auto md:m-0">
                                    <thead>
                                        <tr>
                                            <th></th>
                                            <th>1</th>
                                            <th>2</th>
                                            <th>3</th>
                                            <th>4</th>
                                            <th>Final</th>
                                        </tr>
                                    </thead>

                                    <tbody>
                                        <tr>
                                            <td className="pr-4 border-b border-gray-200 whitespace-nowrap">Patriots</td>
                                            {/* {scores.us.map((score, ind) => {
                                                return <td key={ind} className="px-3 md:px-6 py-1 border border-gray-200">{score}</td>
                                            })} */}

                                            {/* {oo.map(([key, value]) => {
                                                return <td key={key} className="px-3 md:px-6 py-1 border border-gray-200">{value}</td>
                                            })} */}

                                            {usScores.map((score, ind) => {
                                                return <td key={score} className="px-3 md:px-6 py-1 border border-gray-200">{game[score]}</td>
                                            })}

                                            <td className="px-3 md:px-6 py-1 border border-gray-200">{game.goals_for}</td>
                                        </tr>
                                        <tr>
                                            <td className="pr-4 whitespace-nowrap">{opponent}</td>
                                            {/* {scores.opponent.map((score, ind) => {
                                                return <td key={ind} className="px-3 md:px-6 py-1 border border-gray-200">{score}</td>
                                            })} */}

                                            {opponentScores.map((score, ind) => {
                                                return <td key={score} className="px-3 md:px-6 py-1 border border-gray-200">{game[score]}</td>
                                            })}

                                            <td className="px-3 md:px-6 py-1 border border-gray-200">{game.goals_against}</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        )}

                    </div>

                    {!has_been_played ? (
                        <div className="bg-white p-3 sm:p-6 mb-6 shadow-sm overflow-scroll">
                            <p className="text-center">Game stats are not yet available</p>
                        </div>
                    ): (
                        <>
                            <div className="bg-white p-3 sm:p-6 mb-6 shadow-sm overflow-scroll">
                                <Title>Team Stats</Title>
                                <table className="">
                                    <thead>
                                        <tr>
                                            <th></th>
                                            <th className="pb-1 text-xs font-normal">G</th>
                                            <th className="pb-1 text-xs font-normal">GB</th>
                                            <th className="pb-1 text-xs font-normal">SH</th>
                                            <th className="pb-1 text-xs font-normal">FO</th>
                                            <th className="pb-1 text-xs font-normal">PIM</th>
                                        </tr>
                                    </thead>

                                    <tbody>
                                        <tr className="border-b border-mpred">
                                            <td className="md:pr-4 whitespace-nowrap w-full border-r border-mpred md:w-auto">Patriots</td>
                                            {/* {Object.keys(team_stats).map(item => {
                                                return <td key={item} className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{team_stats[item].us}</td>
                                            })} */}

                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{game.us_goals_for}</td>
                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{game.us_ground_balls}</td>
                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">add</td>
                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{game.us_faceoffs_won}</td>
                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{game.us_penalties_in_minutes}</td>
                                        </tr>
                                        <tr>
                                            <td className="pr-1 md:pr-4 whitespace-nowrap w-full border-r border-mpred md:w-auto">{opponent}</td>
                                            {/* {Object.keys(team_stats).map(item => {
                                                return <td key={item} className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{team_stats[item].opponent}</td>
                                            })} */}

                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{game.opponent_goals_for}</td>
                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{game.opponent_ground_balls}</td>
                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">add</td>
                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{game.opponent_faceoffs_won}</td>
                                            <td className="px-2 py-1 text-lg font-bold text-mpblue sm:px-6 sm:text-3xl">{game.opponent_penalties_in_minutes}</td>
                                        </tr>
                                    </tbody>
                                </table>

                            </div> 

                            <div className="bg-white p-3 sm:p-6 mb-6 shadow-sm overflow-scroll">
                                <Table
                                    headers={playerHeaders}
                                    columns={playerColumns}
                                    body={player_stats}
                                    title="Player Stats"
                                />
                            </div>

                            <div className="bg-white p-3 sm:p-6 mb-6 shadow-sm overflow-scroll">
                                <Table
                                    headers={goalieHeaders}
                                    columns={goalieColumns}
                                    body={goalie_stats}
                                    title="Goalie Stats"
                                />
                            </div>
                        </>
                    )}
                </>
            )}




        </main>
    )
}

