import { Table } from "../components";

const PLAYER = {
    first_name: 'Jerry',
    last_name: 'Johnston',
    number: '22',
    positions: ['Attack', 'Middie'],

    career: {
        games_played: 31, goals: 15, goals_per_game: 0.1, assists: 6, assists_per_game: 0, points: 21, points_per_game: 0.1
    },
    seasons: [
        { season_id: 1, name: '2020', games_played: 4, goals: 4, assists: 4, points: 8 },
        { season_id: 2, name: '2021', games_played: 14, goals: 29, assists: 10, points: 39 },
        { season_id: 3, name: '2022', games_played: 9, goals: 16, assists: 7, points: 23 },
    ]
}

const Player = () => {
    const { career } = PLAYER;


    const headers = [
        { label: 'Name', className: 'whitespace-nowrap', default: true },
        // { label: 'Player', sort: 'last_name', className: 'whitespace-nowrap' },
        { label: 'GP', sort: 'games_played', alt: 'Games Played' },
        { label: 'G', sort: 'goals' },
        // { label: 'G/G', sort: 'goals_per_game' },
        { label: 'A', sort: 'assists' },
        // { label: 'A/G', sort: 'assists_per_game' },
        { label: 'PTS', sort: 'points' },
        // { label: 'P/G', sort: 'points_per_game' },
    ];

    const columns = {
        // number: 'number',
        // name: {
        //     type: 'link',
        //     format: '/players/$id',
        //     as: '$first_name $last_name',
        //     // as: 'View Details',
        //     // className: 'w-0 whitespace-nowrap'
        // },
        name: 'string',
        games_played: 'number',
        goals: 'number',
        // goals_per_game: 'number',
        assists: 'number',
        // assists_per_game: 'number',
        points: 'number',
        // points_per_game: 'number',

        // email: 'string',
        // amount: 'currency',
        // subscription: {
        //     type: 'boolean',
        //     format: <svg className="transition duration-200 opacity-1 w-4 h-4 text-purple pointer-events-none" viewBox="0 0 172 172"><g fill="none" strokeWidth="none" strokeMiterlimit="10" fontFamily="none" fontWeight="none" fontSize="none" textAnchor="none" style={{mixBlendMode:'normal'}}><path d="M0 172V0h172v172z"/><path d="M145.433 37.933L64.5 118.8658 33.7337 88.0996l-10.134 10.1341L64.5 139.1341l91.067-91.067z" fill="currentColor" strokeWidth="1"/></g></svg>,
        //     className: 'justify-center flex',
        // },
    }

    return (
        <div className="bg-red-400">
            lmao

            <div className="bg-white p-6 mb-6">
                <h2 className="text-2xl font-bold">Jerry Johnston</h2>
                <p className="text-sm">#22 | Attack</p>
            </div>

            <div className="bg-white p-6">

                <div className="inline-block md:pr-20 border-b border-black mb-4">
                    <h3 className="text-lg font-bold">Career Stats</h3>
                </div>

                <div className="flex flex-wrap">

                    <div className="w-1/2 sm:w-auto sm:mr-10">
                        <p className="text-sm">Games Played</p>
                        <p className="text-3xl">{career.games_played}</p>
                    </div>

                    <div className="w-1/2 sm:w-auto sm:mr-10">
                        <p className="text-sm">Goals</p>
                        <p className="text-3xl">{career.goals}</p>
                    </div>

                    <div className="w-1/2 sm:w-auto sm:mr-10">
                        <p className="text-sm">Assists</p>
                        <p className="text-3xl">{career.assists}</p>
                    </div>

                    <div className="w-1/2 sm:w-auto sm:mr-10">
                        <p className="text-sm">Points</p>
                        <p className="text-3xl">{career.points}</p>
                    </div>
                </div>

            </div>


            <Table
                headers={headers}
                columns={columns}
                body={PLAYER.seasons}
                title="Players"
                uniqueKey="season_id"
            />



        </div>
    )
}

export default Player;
