import { Link, Routes, Route, Outlet } from "react-router-dom";
import Players from "./pages/Players";
import Player from "./pages/Player";
import Invoices from "./pages/invoices";
import Games from "./pages/Games";

const GAMES = [
    { id: 1, home: true, opponent: 'Sequoia', date: new Date(), location: '', notes: 'notes about game' },
    { id: 2, home: false, opponent: 'Peachtree Ridge', date: new Date(), location: '', notes: 'notes about game' }
]


function App() {
    return (
        <Routes>
            <Route path="/" element={<Main />}>
                <Route path="players" element={<Players />} />
                <Route path="players/:player_id" element={<Player />} />
                <Route path="games" element={<Games />} />
                <Route path="invoices" element={<Invoices />} />
                <Route
                    path="*"
                    element={
                        <main style={{ padding: "1rem" }}>
                        <p>404 - There's nothing here!</p>
                        </main>
                    }
                />
            </Route>
        </Routes>
    )
}

function Main() {
    return (
        <div className="min-h-screen bg-gray-100 px-3 md:px-16">
            <div className="w-100 m-auto bg-pink-100 max-w-7xl	">

                <div className="bg-white p-3" style={{background: 'rgba(29, 55, 108, 0.05)'}}>
                    {/* header component here */}

                    <Link to="/">Home</Link> |{" "}
                    <Link to="/players">Players</Link> |{" "}
                    <Link to="/games">Games</Link>

                </div>
                <div className="bg-mpblue h-1 w-full"></div>
                <div className="bg-mpred h-1 w-full"></div>
            </div>


            <Outlet />

            <div className="flex">
                {GAMES.map(item => {
                    return (
                        <div className="p-4 bg-white mx-2">
                            <h3 className="text-center">
                                {item.home ? 'VS' : 'AT'}
                                <br /> 
                                {item.opponent}
                            </h3>

                        </div>
                    )
                })}
            </div>

        </div>
    );
}

export default App;
