// import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
// import classnames from 'classnames';
import { Table } from "../components";

const PLAYERS = [
  { id: 1, first_name: 'John', last_name: 'Hicks', number: 22, graduation_year: 2024, games_played: 4, goals: 19, goals_per_game: 4.75, assists: 4, assists_per_game: 1, points: 23, points_per_game: 5.8 },
  { id: 2, first_name: 'Aiden', last_name: 'Carlock', number: 24, graduation_year: 2024, games_played: 6, goals: 9, goals_per_game: 1.5, assists: 1, assists_per_game: 0.1, points: 10, points_per_game: 1.6 },
  { id: 3, first_name: 'Jack', last_name: 'Cendoya', number: 55, graduation_year: 2022, games_played: 6, goals: 1, goals_per_game: 0.1, assists: 0, assists_per_game: 0, points: 1, points_per_game: 0.1 },
]

export default function Players() {
    let params = useParams();
    console.log(params, 'param')


    const headers = [
        { label: '#', sort: 'number', className: 'whitespace-nowrap', default: true },
        { label: 'Player', sort: 'last_name', className: 'whitespace-nowrap' },
        { label: 'GP', sort: 'games_played', alt: 'Games Played' },
        { label: 'G', sort: 'goals' },
        { label: 'G/G', sort: 'goals_per_game' },
        { label: 'A', sort: 'assists' },
        { label: 'A/G', sort: 'assists_per_game' },
        { label: 'PTS', sort: 'points' },
        { label: 'P/G', sort: 'points_per_game' },
    ];

    const columns = {
        number: 'number',
        name: {
            type: 'link',
            format: '/players/$id',
            as: '$first_name $last_name',
            // as: 'View Details',
            // className: 'w-0 whitespace-nowrap'
        },
        games_played: 'number',
        goals: 'number',
        goals_per_game: 'number',
        assists: 'number',
        assists_per_game: 'number',
        points: 'number',
        points_per_game: 'number',

        // email: 'string',
        // amount: 'currency',
        // subscription: {
        //     type: 'boolean',
        //     format: <svg className="transition duration-200 opacity-1 w-4 h-4 text-purple pointer-events-none" viewBox="0 0 172 172"><g fill="none" strokeWidth="none" strokeMiterlimit="10" fontFamily="none" fontWeight="none" fontSize="none" textAnchor="none" style={{mixBlendMode:'normal'}}><path d="M0 172V0h172v172z"/><path d="M145.433 37.933L64.5 118.8658 33.7337 88.0996l-10.134 10.1341L64.5 139.1341l91.067-91.067z" fill="currentColor" strokeWidth="1"/></g></svg>,
        //     className: 'justify-center flex',
        // },
    }


    return (
        // <main style={{ padding: "1rem 0" }}>
        <main className="px-5">

            <div className="bg-white p-3 sm:p-6 mb-6">
                <Table
                    headers={headers}
                    columns={columns}
                    body={PLAYERS}
                    title="Players"
                />
            </div>
            {/* <div className="flex">
                {PLAYERS.map(item => {
                    return (
                        <div className="p-2 bg-white mx-2">
                            <h3 className="text-center">
                                {item.first_name} {item.last_name}
                            </h3>

                        </div>
                    )
                })}
            </div> */}
        </main>
    );
}
