import { useState } from "react";

const PopConfirm = ({ options, onChange, value, disabled }) => {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    // const [selectedValue, setSelectedValue] = useState(value || '');

    // const handleChange = (option) => {
    //     setDropdownOpen(false);
    //     setSelectedValue(option);
    //     onChange(option);
    // }

    return (
        <div className="relative">
            <div onClick={() => setDropdownOpen(false)} className={`${!dropdownOpen ? 'opacity-0 pointer-events-none' : 'opacity-10'} transition duration-200 fixed bg-black inset-0 z-50 `}></div>

            <div className="relative inline bg-yellow-100">


                <button
                    type="button"
                    disabled={disabled}
                    onClick={() => setDropdownOpen(!dropdownOpen)}
                    aria-expanded={dropdownOpen}
                    // className={`${dropdownOpen ? 'z-50' : ''} disabled:text-gray-400 disabled:border-gray-400 disabled:cursor-not-allowed bg-white relative mt-1 text-left rounded form-input border border-gray-300 w-full px-3 py-2 sm:py-1 text-gray-500 hover:text-gray-600 font-medium hover:border-gray-400 focus:border-gray-400`}
                >
                    Delete
                </button>

                {/* <div className={`${!dropdownOpen ? 'opacity-0 pointer-events-none' : 'opacity-100'} w-full transition duration-200 z-50 origin-top-right absolute top-full left-0 right-auto md:left-auto md:right-0 min-w-56 bg-white border border-gray-200 rounded shadow-lg overflow-hidden mt-1 enter-done`}> */}
                <div className={`${!dropdownOpen ? 'opacity-0 pointer-events-none' : 'opacity-100'} transition duration-200 p-2 z-50 origin-top-right absolute bottom-full left-0 right-auto min-w-56 bg-white rounded shadow-lg overflow-hidden mb-2 enter-done whitespace-nowrap	`}>

                    {/* <div className="bg-red-300 inline-block absolute bottom-full mb-2 p-2"> */}
                        <p className="text-sm pb-2 text-center">Are you sure?</p>

                        <button
                            onClick={() => setDropdownOpen(!dropdownOpen)}
                            className="text-xs px-1 py-0.5 mr-3 text-gray-600 hover:bg-gray-100"
                        >
                            Cancel
                        </button>
                        <button className="text-xs px-1 py-0.5 bg-mpred-200 text-white hover:bg-mpred">Confirm</button>

                        {/* <div class="w-5 overflow-hidden inline-block absolute left-1/2 top-full">
                            <div class=" h-3 w-3 bg-black -rotate-45 transform origin-top-left"></div>
                        </div> */}
                    {/* </div> */}

                    {/* <ul>
                        {options.map(option => {
                            return (
                                <li key={option.label} className="px-2 py-1 cursor-pointer hover:bg-gray-100" onClick={() => handleChange(option)}>{option.label}</li>
                            )
                        })}
                    </ul> */}
                </div>
            </div>

        </div>
    )
}

export default PopConfirm;
