import { createSlice } from '@reduxjs/toolkit'
import { request } from '../../request'

const initialState = {
  players: [],
}

export const playersSlice = createSlice({
    name: 'players',
    initialState,
    reducers: {
        getPlayersR: (state, action) => {
            console.log(action,' acfctiotnn')
            state.players.push(action)
        },
        increment: (state) => {
            // Redux Toolkit allows us to write "mutating" logic in reducers. It
            // doesn't actually mutate the state because it uses the Immer library,
            // which detects changes to a "draft state" and produces a brand new
            // immutable state based off those changes
            state.value += 1
        },
        decrement: (state) => {
            state.value -= 1
        },
        incrementByAmount: (state, action) => {
            state.value += action.payload
        },
    },
})


export const getAllPlayers = (data) => async (dispatch) => {
    try {
        const data = await request({ url: '/api/players', method: 'GET' });
        console.log(data, 'daattaaa')
        if (!data) return alert("error in getAllPlayers")
    //   const response = await axios.get(`${API_URL}/${data}`);
        dispatch(getPlayersR(data));
    } catch (err) {
        throw new Error(err);
    }
};


// Action creators are generated for each case reducer function
export const { increment, decrement, incrementByAmount, getPlayersR } = playersSlice.actions

export default playersSlice.reducer
