    goals: 0,
    assists: 0,
    sog: 0,
    ground_balls: 0,
    interceptions: 0,
    takeaways: 0,
    unforced_errors: 0,
    penalties: 0,
    penalties_in_minutes: 0,
}

const initialStateTypes = {
    goals: 'Goals',
    assists: 'Assists',
    sog: 'Shots on Goal',
    ground_balls: 'Ground Balls',
    interceptions: 'Interceptions',
    takeaways: 'Takeaways',
    unforced_errors: 'Unforced Errors',
    penalties: 'Penalties',
    penalties_in_minutes: 'Penalties In Minutes',
}


